import React, { Component } from 'react'
import { Form, Button } from 'react-bootstrap'
class UserForm extends Component {
    state = {
        firstName: '', 
        lastName: ''
    };

    handleChange = (e) => {
        // console.log(e.target.name, e.target.value);

        const { name, value } = e.target;
        this.setState({
            [name]: value
        });

    }

    render() {
        return (
            <div className="container">
                <Form>
                    <Form.Group controlId="formFirstName">
                        <Form.Label>First Name</Form.Label>
                        <Form.Control type="text" name="firstName" value={this.state.firstName} onChange={this.handleChange} placeholder="Enter your first name" />
                    </Form.Group>

                    <Form.Group controlId="formFirstName">
                        <Form.Label>Last Name</Form.Label>
                        <Form.Control type="text" name="lastName" value={this.state.firstName} onChange={this.handleChange} placeholder="Enter your last name" />
                    </Form.Group>


                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                    </Form.Group>
                    <Form.Group controlId="formBasicCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>
                <br></br>
                <br></br>

                <br></br>

                <prev>{JSON.stringify(this.state, null, 2)}</prev>

            </div>


        )
    }
}

export default UserForm
